let mealsList=document.getElementById("meals"),ShowMeals=document.getElementById("mealsContainer"),mealsNames=["pizza","carrot","corn","potato","tomato","asparagus","lettuce","radish","oregano","blackberry","coconut","cake","poutine","rendang","duck","ham","salami","curry"],Selectlist="",ourMeals="",posts=[],httpRequest=new XMLHttpRequest;for(i=0;i<mealsNames.length;i++)Selectlist+=`<option value="${mealsNames[i]}">${mealsNames[i]}</option>`;function GetReqest(e="pizza"){httpRequest.open("Get",`https://forkify-api.herokuapp.com/api/search?q=${e}`),httpRequest.send(),httpRequest.onreadystatechange=()=>{if(4==httpRequest.readyState){let e=JSON.parse(httpRequest.response);posts=e.recipes,LoadMeals()}}}function LoadMeals(){for(i=0;i<posts.length;i++)ourMeals+=`\n            <div class="card col-lg-3 col-md-4 col-6 flex-grow-1">\n                <img src="${posts[i].image_url}" class="card-img-top img" alt="...">\n                <div class="card-body text-center">\n                    <h5 class="card-title">${posts[i].title}</h5>\n                    <a href="html/MoreInfo.html?recipe_id=${posts[i].recipe_id}" class="btn btn-primary">Read More</a>\n                </div>\n            </div> \n        `;ShowMeals.innerHTML=ourMeals}mealsList.innerHTML=Selectlist,GetReqest(),mealsList.onchange=e=>{ourMeals="",GetReqest(mealsList.value)};