let recipe_id=new URLSearchParams(window.location.search).get("recipe_id"),infoContainer=document.getElementById("infoContainer"),details="",info={},ingredients=[],ingredientsLsit="",httpReques=new XMLHttpRequest;function loadContant(e){details+=`\n        <div class="row"> \n            <img src="${e.recipe.image_url}" alt="" class="img-fluid col-md-6 col-12 imgInfo">\n            <div class="offset-1 col-md-5 col-12 d-inline">\n                <h2 class="pb-5">${e.recipe.title}</h2>\n                <h5 class="pb-3">ingredients :</h5>\n                <ul id="ingredients">\n                </ul>\n                <div class="py-5">\n                    <span>publisher :  ${e.recipe.publisher}</span>\n                </div>\n            </div>\n        </div>\n    `,infoContainer.innerHTML=details}function loadingredients(e){for(i=0;i<e.length;i++)ingredientsLsit+=`\n            <li>${e[i]}</li>\n        `;document.getElementById("ingredients").innerHTML=ingredientsLsit}httpReques.open("Get",`https://forkify-api.herokuapp.com/api/get?rId=${recipe_id}`),httpReques.send(),httpReques.onreadystatechange=()=>{4==httpReques.readyState&&(info=JSON.parse(httpReques.response),ingredients=info.recipe.ingredients,loadContant(info),loadingredients(ingredients))};